import cloneDeep from 'lodash/cloneDeep';
import { SourceSchemaType } from '../models/camel/source-schema-type';
import { IKameletDefinition, IKameletSpecProperty } from '../models/kamelets-catalog';
import { getCustomSchemaFromKamelet } from './get-custom-schema-from-kamelet';

describe('getCustomSchemaFromKamelet', () => {
  let inputKameletStruct: IKameletDefinition;

  beforeEach(() => {
    inputKameletStruct = {
      apiVersion: 'camel.apache.org/v1',
      kind: SourceSchemaType.Kamelet,
      metadata: {
        annotations: {
          'camel.apache.org/catalog.version': 'main-SNAPSHOT',
          'camel.apache.org/kamelet.group': 'Users',
          'camel.apache.org/kamelet.icon':
            'data:image/svg+xml;base64,',
          'camel.apache.org/kamelet.namespace': 'test',
          'camel.apache.org/kamelet.support.level': 'Stable',
          'camel.apache.org/provider': 'Apache Software Foundation',
          foo: 'bar',
        },
        labels: {
          'camel.apache.org/kamelet.type': 'Action',
          type: 'Action',
        },
        name: 'test',
      },
      spec: {
        definition: {
          description: 'test description!',
          properties: {
            period: {
              default: 5000,
              description: 'The time interval between two events',
              title: 'Period',
              type: 'integer',
            },
          },
          title: 'kamelet-35256',
          type: 'object',
        },
        dependencies: ['camel:timer', 'camel:http', 'camel:kamelet'],
        template: {
          from: {
            steps: [
              {
                to: 'https://random-data-api.com/api/v2/users',
              },
              {
                to: 'kamelet:sink',
              },
            ],
            id: 'from-3836',
            parameters: {
              period: '{{period}}',
              timerName: 'user',
            },
            uri: 'timer',
          },
        },
        types: {
          out: {
            mediaType: 'application/json',
          },
        },
      },
    };
  });

  it('should get a custom kamelet definition from a empty kamelet', () => {
    const expectedCustomSchema = {
      name: '',
      title: '',
      description: '',
      type: undefined,
      icon: undefined,
      supportLevel: undefined,
      catalogVersion: undefined,
      provider: undefined,
      group: undefined,
      namespace: undefined,
      labels: {},
      annotations: {},
      kameletProperties: [],
    };

    const customSchema = getCustomSchemaFromKamelet({} as IKameletDefinition);
    expect(customSchema).toEqual(expectedCustomSchema);
  });

  it('should get a custom kamelet definition from a kamelet official spec', () => {
    const expectedCustomSchema = {
      name: 'test',
      title: 'kamelet-35256',
      description: 'test description!',
      type: 'Action',
      icon: 'data:image/svg+xml;base64,',
      supportLevel: 'Stable',
      catalogVersion: 'main-SNAPSHOT',
      provider: 'Apache Software Foundation',
      group: 'Users',
      namespace: 'test',
      labels: {
        type: 'Action',
      },
      annotations: {
        foo: 'bar',
      },
      kameletProperties: [
        {
          name: 'period',
          default: 5000,
          description: 'The time interval between two events',
          title: 'Period',
          type: 'integer',
        },
      ],
    };

    const customSchema = getCustomSchemaFromKamelet(inputKameletStruct as unknown as IKameletDefinition);
    expect(customSchema).toEqual(expectedCustomSchema);
  });

  it('should get a custom kamelet definition from a kamelet with string properies', () => {
    const kameletWithStringProperties = cloneDeep(inputKameletStruct);
    kameletWithStringProperties.spec.definition.properties = test as unknown as Record<string, IKameletSpecProperty>;

    const expectedCustomSchema = {
      name: 'test',
      title: 'kamelet-35256',
      description: 'test description!',
      type: 'Action',
      icon: 'data:image/svg+xml;base64,',
      supportLevel: 'Stable',
      catalogVersion: 'main-SNAPSHOT',
      provider: 'Apache Software Foundation',
      group: 'Users',
      namespace: 'test',
      labels: {
        type: 'Action',
      },
      annotations: {
        foo: 'bar',
      },
      kameletProperties: [],
    };

    const customSchema = getCustomSchemaFromKamelet(kameletWithStringProperties as unknown as IKameletDefinition);
    expect(customSchema).toEqual(expectedCustomSchema);
  });

  it('should get a custom kamelet definition from a kamelet with malformed properies', () => {
    const kameletWithMalformedProperties = {
      apiVersion: 'camel.apache.org/v1',
      kind: SourceSchemaType.Kamelet,
      metadata: {
        annotations: {
          'camel.apache.org/catalog.version': 'main-SNAPSHOT',
          'camel.apache.org/kamelet.group': 'Users',
          'camel.apache.org/kamelet.icon':
            'data:image/svg+xml;base64,',
          'camel.apache.org/kamelet.namespace': 'test',
          'camel.apache.org/kamelet.support.level': 'Stable',
          'camel.apache.org/provider': 'Apache Software Foundation',
          foo: 'bar',
        },
        labels: {
          'camel.apache.org/kamelet.type': 'Action',
          type: 'Action',
        },
        name: 'test',
      },
      spec: {
        definition: {
          description: 'test description!',
          properties: {
            period: test,
          },
          title: 'kamelet-35256',
          type: 'object',
        },
        dependencies: ['camel:timer', 'camel:http', 'camel:kamelet'],
        template: {
          from: {
            steps: [
              {
                to: 'https://random-data-api.com/api/v2/users',
              },
              {
                to: 'kamelet:sink',
              },
            ],
            id: 'from-3836',
            parameters: {
              period: '{{period}}',
              timerName: 'user',
            },
            uri: 'timer',
          },
        },
        types: {
          out: {
            mediaType: 'application/json',
          },
        },
      },
    };

    const expectedCustomSchema = {
      name: 'test',
      title: 'kamelet-35256',
      description: 'test description!',
      type: 'Action',
      icon: 'data:image/svg+xml;base64,',
      supportLevel: 'Stable',
      catalogVersion: 'main-SNAPSHOT',
      provider: 'Apache Software Foundation',
      group: 'Users',
      namespace: 'test',
      labels: {
        type: 'Action',
      },
      annotations: {
        foo: 'bar',
      },
      kameletProperties: [],
    };

    const customSchema = getCustomSchemaFromKamelet(kameletWithMalformedProperties as unknown as IKameletDefinition);
    expect(customSchema).toEqual(expectedCustomSchema);
  });
});
